CREATE TABLE `athletes` (
  `athlete_id` int(5) NOT NULL AUTO_INCREMENT,
  `created_date` datetime NOT NULL,
  `lastlogin_date` datetime NOT NULL,
  `email` varchar(255) NOT NULL,
  `password` varchar(255) NOT NULL,
  `api_key` varchar(255) NOT NULL,
  `name` varchar(255) NOT NULL,
  `weight` int(3) NOT NULL,
  `1s` int(4) NOT NULL,
  `5s` int(4) NOT NULL,
  `30s` int(4) NOT NULL,
  `1m` int(4) NOT NULL,
  `5m` int(3) NOT NULL,
  `10m` int(3) NOT NULL,
  `20m` int(3) NOT NULL,
  `60m` int(3) NOT NULL,
  PRIMARY KEY (`athlete_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=latin1;

CREATE TABLE `activities` (
  `activity_id` int(5) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `shortname` varchar(255) NOT NULL,
  `type` varchar(25) NOT NULL,
  `wu` text,
  `desc` text,
  `info` text,
  `target` text,
  `cd` text,
  `duration` int(4),
  PRIMARY KEY (`activity_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=latin1;

CREATE TABLE `schedules` (
  `schedule_id` int(5) NOT NULL AUTO_INCREMENT,
  `athlete_id` int(5) NOT NULL,
  `week` varchar(2) NOT NULL,
  `year` varchar(2) NOT NULL,
  `type` varchar(25) NOT NULL,
  `sun` varchar(25) NOT NULL,
  `sun_dur` int(4),
  `mon` varchar(25) NOT NULL,
  `mon_dur` int(4),
  `tue` varchar(25) NOT NULL,
  `tue_dur` int(4),
  `wed` varchar(25) NOT NULL,
  `wed_dur` int(4),
  `thu` varchar(25) NOT NULL,
  `thu_dur` int(4),
  `fri` varchar(25) NOT NULL,
  `fri_dur` int(4),
  `sat` varchar(25) NOT NULL,
  `sat_dur` int(4),
  PRIMARY KEY (`schedule_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=latin1;




